INSERT INTO `hui_job` VALUES ('000001', '跟单助理', '广州市天河区天平架沙太南路', '3000~4000', '1505302522', '<p><span style=\"display: inline !important; float: none; background-color: transparent; color: rgb(51, 51, 51); font-family: &quot;Microsoft Yahei&quot;; font-size: 12px; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; line-height: 24px; orphans: 2; text-align: left; text-decoration: none; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; word-spacing: 0px; word-wrap: break-word;\">岗位职责：1、大专以上学历，20-40岁，品质佳，有团队意识。</span><br/><span style=\"display: inline !important; float: none; background-color: transparent; color: rgb(51, 51, 51); font-family: &quot;Microsoft Yahei&quot;; font-size: 12px; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; line-height: 24px; orphans: 2; text-align: left; text-decoration: none; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; word-spacing: 0px; word-wrap: break-word;\">2、认真、负责，有上进心。</span><br/><span style=\"display: inline !important; float: none; background-color: transparent; color: rgb(51, 51, 51); font-family: &quot;Microsoft Yahei&quot;; font-size: 12px; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; line-height: 24px; orphans: 2; text-align: left; text-decoration: none; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; word-spacing: 0px; word-wrap: break-word;\">3、吃苦耐劳，主动思考问题，协助经理做好份内工作。</span><br/><span style=\"display: inline !important; float: none; background-color: transparent; color: rgb(51, 51, 51); font-family: &quot;Microsoft Yahei&quot;; font-size: 12px; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; line-height: 24px; orphans: 2; text-align: left; text-decoration: none; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; word-spacing: 0px; word-wrap: break-word;\">4、协助经理做好优秀人才的引进，简单的初试。</span><br/><span style=\"display: inline !important; float: none; background-color: transparent; color: rgb(51, 51, 51); font-family: &quot;Microsoft Yahei&quot;; font-size: 12px; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; line-height: 24px; orphans: 2; text-align: left; text-decoration: none; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; word-spacing: 0px; word-wrap: break-word;\">5、老客户的售后电话回访，资料的变更。</span><br/><span style=\"display: inline !important; float: none; background-color: transparent; color: rgb(51, 51, 51); font-family: &quot;Microsoft Yahei&quot;; font-size: 12px; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; line-height: 24px; orphans: 2; text-align: left; text-decoration: none; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; word-spacing: 0px; word-wrap: break-word;\">6、协助做好团队人员的管理工作。</span><br/><span style=\"display: inline !important; float: none; background-color: transparent; color: rgb(51, 51, 51); font-family: &quot;Microsoft Yahei&quot;; font-size: 12px; font-style: normal; font-variant: normal; font-weight: 400; letter-spacing: normal; line-height: 24px; orphans: 2; text-align: left; text-decoration: none; text-indent: 0px; text-transform: none; -webkit-text-stroke-width: 0px; word-spacing: 0px; word-wrap: break-word;\">7、上五休二，每天8:15-17:00，国假日正常休息</span></p>');
